import Thongtin from './thongtin';
import SanPham from './sanpham';
import ShoppingCart from './ShoppingCart';
import ReceiverInfo from './ReceiverInfo';
import PaymentMethod from './PaymentMethod';

function giohang() {
  const products = [
    {
      'ten':'loại Sản Phẩm 1',
      'ds_san_pham':[
        {
          'id': 1,
          'hinh_anh':'./2.jpg',
          'ten':'Sản Phẩm 1',
          'gia':'chỉ được xem',
          'mota':'vip1',
        },
        {
          'hinh_anh':'./1.jpg',
          'ten':'Sản Phẩm 2',
          'gia':'1000',
          'mota':'vip2',
        },
        {
          'hinh_anh':'./1.jpg',
          'ten':'Sản Phẩm 3',
          'gia':'1000',
          'mota':'vip2',
        },
      ]
    },
    {
      'ten':'Loại Sản Phẩm 2',
      'ds_san_pham':[
        {
          'hinh_anh':'1.jpg',
          'ten':'Sản Phẩm 3',
          'gia':'1000',
          'mota':'vip3',
        },
        {
          'hinh_anh':'1.jpg',
          'ten':'Sản Phẩm 4',
          'gia':'10000',
          'mota':'vip4',
        },
      ]
      
      
    },
    {
      'ten':'Loại Sản Phẩm 3',
      'ds_san_pham':[
        {
          'hinh_anh':'1.jpg',
          'ten':'Sản Phẩm 5',
          'gia':'1000',
          'mota':'vip5',
        },
        {
          'hinh_anh':'1.jpg',
          'ten':'Sản Phẩm 6',
          'gia':'1000',
          'mota':'vip6',
        },
      ]
      
      
    }
  ];


  const handleQuantityChange = (product, quantity) => {
    return (
      <>

        <div className="giohang">
          <ShoppingCart products={products} onQuantityChange={handleQuantityChange} />
          <ReceiverInfo />
          <PaymentMethod />
          <button>Thanh toán</button>
        </div>

      </>
    );
  }
}
export default giohang;
